package com.syntax.class07;

public class IncrementAndDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=10;
		num=num+1;
		num+=1;
		num++;
		
		System.out.println(num);//13
		
		//increment operator add 1 to a variable
		
		num++;
		System.out.println(num);
		
		//10++; CE: invalid argument to operation ++/--
		
		//decrement operator subtract 1 from variable
		
		num--;
		num--;
		num--;
		System.out.println(num); //11
		
		//Loop statement repeats a block of code as long as the condition is true
		/*
		 * Why do we need them?
		 * When we want to repeat the same block of code multiple times
		 * we use Loops:
		 * -while loop
		 * -do while loop
		 * -for loop
		 * -enhanced for loop(advanced/for each loop)
		 * 
		 * 
		 * 
		 */
	}

}
